import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { addCategory } from '../../redux/slices/categoriesSlice'

export default function CategoryForm({ type }) {
    const dispatch = useDispatch()
    const [name, setName] = useState('')

    const handleSubmit = (e) => {
        e.preventDefault()
        if (!name.trim()) return
        dispatch(addCategory({ name, type }))
        setName('')
    }

    return (
        <form onSubmit={handleSubmit} className="mt-2">
            <input
                value={name}
                onChange={e => setName(e.target.value)}
                placeholder={`Add ${type} category`}
                className="border p-1 mr-2 rounded dark:bg-gray-700 dark:text-white"
            />
            <button className="bg-green-600 text-white px-3 rounded">+</button>
        </form>
    )
}
